        if(size){
            objSelect.style.width = size;
        } else {
            for (var i=0; i&lt; objSelect.options.length; i++){
                if (objSelect[i].text.length &gt; maxlength){
                    maxlength = objSelect[i].text.length;
                }
            }
            objSelect.style.width = maxlength * 9;
        }
    } 
}

