  var nmo = date.getMonth() + 1;
  var nd = date.getDate();
  var nh = date.getHours();
  var nmi = date.getMinutes();
  var ns = date.getSeconds();
  return ny == y &amp;&amp; nmo == mo &amp;&amp; nd == d &amp;&amp; nh == h &amp;&amp; nmi == mi &amp;&amp; ns == s;
}
