{
    TOwner *owner_;

public:
    property(TOwner *owner)
        : owner_(owner) {}

    TValue value() const
    {
        return owner_-&gt;get_property(TKey());
    }

    operator TValue() const
    {
        return value();
    }

    TValue operator=(const TValue &amp;value)
    {
        owner_-&gt;set_property(TKey(), value);
        return value;
    }
};

