function generateNextSet(set){
  for (var i = 0; i &lt; set.length; i++){
    var amount = set[i];
    if (amount + 1 &gt; limits[i]){
      set[i] = 0;
    } else {
      set[i] = amount + 1;
      return set;
    }
  }
  return false;
}

function generateSets(){
  var initial_set = [0, 0, 0, 0]
  var set = generateNextSet(initial_set);
  out(set);
  while (set = generateNextSet(set)) {
    out(set);
  }
};

