function increment(digit) {
    if (digit &gt; 6) {
        return false;
    }

    var value = counter[digit] + 1;

    if (value &gt; items[digit]) {
        counter[digit] = 0;
        return increment(digit + 1);
    }

    counter[digit] = value;

    return true;
}

while (increment(1)) {
    var set = [];

    for (var digit in counter) {
        var value = counter[digit];

        for (var i = 0; i &lt; value; i++) {
            set.push(digit);
        }
    }

