function format_duration(elem) {
var num = parseInt($(elem).text());
var hours = 0;
var mins = 0;
var secs = 0;
while (num &gt; 0) {
  if (num &lt; 60) {
    secs += num;
    num = 0;
  }
  else if (num &lt; 3600) {
    mins++;
    num -= 60;
  }
  else if (num &lt; 86400) {
    hours++;
    num -= 3600;
  }
}
var rv = "";
if (hours &gt; 0) {
    rv += hours+" hours ";
}
if (mins &gt; 0) {
    rv += mins+" mins ";
}
if (secs &gt; 0) { 
    rv += secs+" secs ";
}
$(elem).text(rv);
}

