    return this.each(function() {
        var t = parseInt($(this).text(), 10);
        $(this).data('original', t);
        var h = Math.floor(t / 3600);
        t %= 3600;
        var m = Math.floor(t / 60);
        var s = Math.floor(t % 60);
        $(this).text((h &gt; 0 ? h + ' hour' + ((h &gt; 1) ? 's ' : ' ') : '') +
                     (m &gt; 0 ? m + ' minute' + ((m &gt; 1) ? 's ' : ' ') : '') +
                     s + ' second' + ((s &gt; 1) ? 's' : ''));
    });
};
