];

function convert() {
    var t = parseInt($('#val').val());
    var r = '';
    for (var i = 0; i &lt; tbl.length; i++) {
        var d = tbl[i];
        if (d[0] &lt; t) {
            var u = Math.floor(t / d[0]);
            t -= u * d[0];
            r += u + ' ' + d[1] + (u == 1 ? ' ' : 's ');
        }
    }
    $('#result').html(r);
}

