function timeString( t ) {
    var ret = [], t = {
        hour: parseInt(t/3600),
        minute: parseInt(t/60%60),
        second: t%60
    };

    for ( var n in t )
        t[n] &amp;&amp; ret.push( t[n], n + "s".substr( t[n] &lt; 2 ) );

    return ret.join(" ");
}

