function SendEMail(from, to, subject, body)
{
  try
  {
    var oMsg      = new ActiveXObject("CDO.Message");
    oMsg.From     = from;
    oMsg.To       = to;
    oMsg.Subject  = subject;
    oMsg.TextBody = body;
    oMsg.Send(); 
  }
  catch(e)
  {
    WScript.Echo("Error: " + e.description);
  }
}

