function changetimezone(time_value, tz){
  if(!tz){
    tz = 0;
  }
  var values = time_value.split(" ");
  time_value = values[1] + " " + values[2] + ", " + values[5] + " " + values[3];
  var t = parseInt(Date.parse(time_value))/1000;

  return t + tz * 60;
}

