                   }
                   break;
               case "string":
               case "number":
               case "boolean":
               case "null":
                   return false;
               default:
                   return true;
           }
           stack.push(key);
           for (k in obj) {
               if (obj.hasOwnProperty(k)) {
                   v = getUndefiend(obj[k], k);
                   if (v) {
